import React, { useState } from "react";
import styled from "styled-components";

function Kisi(props) {
  const { veri } = props;
  const [tıklananKişi, setTıklananKişi] = useState(null);
  return (
    <div>
      <p> {veri.name}</p>
      <button> {veri.birth_year}</button>
      {tıklananKişi === veri.name && <div> {veri.gender}</div>}
    </div>
  );
}

export default Kisi;
